var sv, ScriptsVari = {
    settings: {
        $body: $("body"),
        $html: $("html")
    },
    init: function() {
        sv = this.settings, this.bindUIActions(), this.onLoadActions(), this.scrollActions(), 
        this.resizeActions(), this.ieMessage();
    },
    onLoadActions: function() {
        $(window).load(function() {});
    },
    bindUIActions: function() {},
    scrollActions: function() {
        $(window).scroll(function() {});
    },
    resizeActions: function() {
        $(window).resize(function() {}).resize();
    },
    ieMessage: function() {
        if (sv.$html.hasClass("lt-ie9")) {
            var a = "<div id='ie' class='container'>";
            a += "<h1>Problema!</h1>", a += "<p class='intro'>Sembra che tu sia usando un browser abbastanza datato.</p>", 
            a += "<p>Browser vecchi, come Internet Explorer versione 6,7 e 8 non sono supportati dal nostro sito, scaricane uno pi√π recente ai seguenti link:</p>", 
            a += "<ul>", a += "<li><a href='http://www.google.com/chrome' target='_blank'>Google Chrome</a></li>", 
            a += "<li><a href='http://www.mozilla.org/en-US/firefox/new/' target='_blank'>Firefox</a></li>", 
            a += "<li><a href='http://www.apple.com/safari/'' target='_blank'>Safari</a></li>", 
            a += "</ul></div>", $(".main").replaceWith(a);
        }
    }
};

jQuery(document).ready(function() {
    ScriptsVari.init();
});